const About = () => {
  return <div className="container my-3">

    <h1>About Personal NoteBook</h1>
    <h3>➼ It's a MERN stack based web app which is created by Utkarsh sharma for storing your daily notes and to make sure you never forget anything.</h3>
    <h3>➼ Complete security is being provided since proper authentication is getting done so no un-authorised user able to use the app.</h3>
    <h3>➼ JWT is used for passing user data to the server securely.</h3>
    <h3>➼ The user is being provided functionality of all the crud operations.</h3>
    <h3>➼ Route restriction is also implemented so no one able to access the notes without login/sign-in .</h3>

    </div>
};

export default About;
